                if(x < y)
                {
                    var temper = listings[j];
                    listings[j] = listings[j+1];
                    listings[j+1] = temper;
                }
            }
        }
        this.setState({listings: listings});
    }

    sortbynameinc = () => {
        let listings = this.state.listings, n = listings.length;
        for(var i =0; i < n-1; i++)
        {
            for(var j=0; j < n-1; j++)
            {
